<template>
  <div id="step2">
    <ProgressBar>
      <div class="step">
        <span class="step-num">3</span>
        <div class="bar"></div>
        <span class="step-text">14分钟内付款</span>
      </div>
    </ProgressBar>
    <div class="main">
      <div class="left">
        <div class="seat-example">
          <div class="selectable-example example">
            <span>可选座位</span>
          </div>
          <div class="sold-example example">
            <span>已售座位</span>
          </div>
          <div class="selected-example example">
            <span>已选座位</span>
          </div>
          <div class="couple-example example">
            <span>情侣座位</span>
          </div>
        </div>

        <div
          class="seats-block"
          data-cols="13"
          data-section-id="1"
          data-section-name="默认区域"
          data-seq-no="202209190210144"
        >
          <div class="row-id-container">
            <span class="row-id" v-for="(item, i) in seatList">{{ i + 1 }}</span>
          </div>

          <div class="seats-container">
            <div class="screen-container" style="left: 0px">
              <div class="screen">银幕中央</div>
              <div class="c-screen-line"></div>
            </div>

            <div class="seats-main">
              <table cellspacing="0">
                <tr v-for="(tr, index) in seatList">
                  <td
                    v-for="(td, i) in tr"
                    @click="seatClick($event, index, i)"
                    :class="td"
                    :key="i"
                  >
                    {{ i + 1 }}
                  </td>
                </tr>
              </table>
            </div>
          </div>
        </div>
      </div>
      <div class="right">
        <div class="top">
          <img src="../assets/image.jpg" />
          <div>
            <p>{{ currentList.name }}</p>
            <p>类型:{{ currentList.classify.type }}</p>
            <p>时长:{{ currentList.duration }}</p>
          </div>
        </div>
        <div class="bottom">
          <p>影院 :{{ currentList.location[0].brand }}</p>
          <p>影厅 :{{ currentList.location[0].room }}</p>
          <!-- <p>版本 :{{ currentList.location[0]. }}</p> -->
          <p>场次 :{{ currentList.location[0].date }}</p>
          <p>票价 :{{ currentList.location[0].price }}</p>
          <div>
            座位 :
            <template v-for="(itemIndex, index) of seatList">
              <template v-for="(item, i) of itemIndex">
                <div class="ticket" v-if="item === 'selected'">
                  {{ `${index + 1}排${i + 1}座` }}
                </div>
              </template>
            </template>
          </div>
          <p>总价 :{{ totalPrice }} ￥</p>
        </div>
        <el-button @click="goStep3">确认选座</el-button>
      </div>
    </div>
  </div>
</template>

<script>
import ProgressBar from '../components/progressBar.vue';
export default {
  name: 'step2',

  data() {
    return {
      currentList: {},
      seatList: [
        ['false', 'false', 'false', 'false', 'false', 'false'],
        ['false', 'false', 'false', 'false', 'false', 'false'],
        ['false', 'false', 'true', 'false', 'false', 'false'],
        ['false', 'false', 'false', 'false', 'false', 'false'],
        ['false', 'false', 'false', 'true', 'false', 'false'],
      ],
    };
  },
  created() {
    this.currentList = this.$root.getCurrentIdList(this.$route.params.id, this.$route.params.index);
    this.$root.buyTicket = [];
    // console.log('setp2', this.currentList);
    // this.seatList = this.currentList.location[0].seats;
  },
  components: { ProgressBar },
  computed: {
    totalPrice() {
      let sum = 0;
      this.seatList.map(item => {
        item.map(item2 => {
          if (item2 === 'selected') {
            sum += this.currentList.location[0].price;
          }
        });
      });
      return sum;
    },
  },
  methods: {
    seatClick(e, tr, td) {
      if (e.target.classList[0] === 'false') {
        e.target.classList = 'selected';
        // console.log(this.seatList[tr][td]);
        // this.seatList[tr][td] = 'selected';
        this.$set(this.seatList[tr], td, 'selected');
      } else if (e.target.classList[0] === 'selected') {
        e.target.classList = 'false';
        this.$set(this.seatList[tr], td, 'false');
      }
    },
    goStep3() {
      this.seatList.map((item, i) => {
        item.map((item2, j) => {
          if (item2 === 'selected') {
            let obj = {
              title: this.currentList.name,
              time: this.currentList.location[0].date,
              address: this.currentList.location[0].brand,
              seat: `${i + 1}排${j + 1}座`,
              price: this.currentList.location[0].price,
            };

            this.$root.buyTicket.push(obj);
          }
        });
      });
      this.$router.push(`/home/payment/${this.$route.params.id}/${this.$route.params.index}/step3`);
    },
  },
};
</script>

<style lang="scss" scoped>
.main {
  display: flex;
  justify-content: center;
  align-items: center;
  .left {
    width: 75%;
    .seat-example {
      margin: 30px 0 30px 118px;
      .selectable-example {
        background-image: url(data:image/png;base64,);
      }
      .sold-example {
        background-image: url(data:image/png;base64,);
      }
      .selected-example {
        background-image: url(data:image/png;base64,);
      }
      .couple-example {
        padding-left: 77px !important;
        background-image: url(data:image/png;base64,);
      }
      .example {
        display: inline-block;
        font-size: 16px;
        color: #666;
        height: 26px;
        line-height: 26px;
        padding-left: 38px;
        background-repeat: no-repeat;
        margin-right: 50px;
      }
    }

    .seats-block {
      font-size: 0;
      overflow: hidden;
      margin-left: 20px;
      white-space: nowrap;
      .row {
        white-space: nowrap;
        margin-bottom: 10px;
        text-align: center;
      }
      .empty-row {
        height: 20px;
      }
      .screen-container {
        display: inline-block;
        position: absolute;
        top: 0;
        bottom: 0;
        z-index: -1;
        .screen {
          width: 550px;
          padding-top: 50px;
          text-align: center;
          font-size: 16px;
          color: #666;
          background: url(data:image/png;base64,)
            no-repeat;
          background-position-x: center;
          position: relative;
          margin-bottom: 40px;
        }
        .c-screen-line {
          width: 0;
          border-left: 1px dashed #e5e5e5;
          position: absolute;
          top: 90px;
          bottom: 0;
          left: 50%;
        }
      }
      .seats-main {
        width: 100%;
        height: 100%;

        table {
          width: 100%;
          // height: 100%;
          tr {
            .false {
              background-image: url(data:image/png;base64,);
            }
            .true {
              background-image: url(data:image/png;base64,);
            }
            .selected {
              background-image: url(data:image/png;base64,);
            }
            td {
              height: 34px;
              font-size: 12px;
              // background-image: url(data:image/png;base64,);
              background-repeat: no-repeat;
            }
          }
        }
      }
      .row-id-container {
        width: 20px;
        float: left;
        margin-top: 112px;
        white-space: normal;
      }
      .seats-container {
        margin-left: 50px;
        overflow: auto;
        position: relative;
        padding-top: 112px;
      }
      .row-id {
        font-size: 16px;
        color: #999;
        margin-right: 40px;
        margin-bottom: 10px;
        display: inline-block;
        width: 20px;
        height: 26px;
        line-height: 29px;
        text-align: center;
      }
      .empty-row-id {
        display: inline-block;
        height: 20px;
        width: 20px;
      }
      .seats-wrapper {
        display: inline-block;
        min-width: 550px;
      }
    }
  }
  .right {
    width: 25%;
    background-color: #f9f9f9;
    .top {
      display: flex;
      justify-content: flex-start;
      align-items: center;
      img {
        width: 64px;
        height: 90px;
      }
    }
    .bottom {
      display: flex;
      flex-direction: column;
      justify-content: space-evenly;
      align-items: flex-start;
      .ticket {
        cursor: default;
        position: relative;
        font-size: 12px;
        color: #f03d37;
        display: inline-block;
        width: 60px;
        height: 30px;
        line-height: 30px;
        text-align: center;
        margin: 0 12px 10px 0;
        background: url(data:image/png;base64,)
          no-repeat;
      }
    }
  }
}
</style>
