  z-index: 9999;
  text-align: center;
  .mark {
    width: 100vw;
    height: 100vh;
    position: fixed;
    background: rgba(0, 0, 0, 0.2);
    top: 0;
    left: 0;
    z-index: -9999;
    cursor: pointer;
    transition: all 0.15s;
  }
  .center {
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    background-color: #fff;
    z-index: 9999;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    .icon {
      display: inline-block;
